mapper.sql:
select * from (
	select  from departmen C
)T

mapper.update:
UPDATE departmen

	DEPT_NM = #{dept_nm}, 
	SYS_USER_UPDATE = #{user_update}, 
	SYS_TGL_UPDATE = #{tgl_update}, 
	SYS_JAM_UPDATE = #{jam_update}, 
	SYS_USER_CREATE = #{user_create}, 
	SYS_TGL_CREATE = #{tgl_create} 

WHERE DEPT_KD= #{dept_kd}
AND SUBDIV_KD=#{subdiv_kd} 
AND DIVISI_KD = #{divisi_kd}

mapper.delete:
delete from departmen 
where DEPT_KD=#{dept_kd} 
AND SUBDIV_KD=#{subdiv_kd} 
AND DIVISI_KD = #{divisi_kd}

mapper.select:
	
where T.dept_kd=#{dept_kd} 
AND T.subdiv_kd=#{subdiv_kd} 
AND T.divisi_kd = #{divisi_kd}

