mapper.update:
UPDATE group_lokasi_d

	DIVISI_KD = #{divisi_kd}, 
	SUBDIV_KD = #{subdiv_kd}, 
	GROUP_LOK = #{group_lok}, 
	LOK_KD = #{lok_kd}, 
	SYS_USER_CREATE = #{user_create}, 
	SYS_TGL_CREATE = #{tgl_create}, 
	SYS_JAM_CREATE = #{jam_create} 

WHERE  
	DIVISI_KD= #{divisi_kd} 
	AND SUBDIV_KD= #{subdiv_kd}
	AND GROUP_LOK= #{group_lok}
	AND LOK_KD= #{lok_kd}

mapper.delete:
delete from group_lokasi_d
  
	DIVISI_KD= #{divisi_kd} 
	 AND SUBDIV_KD= #{subdiv_kd} 
	 AND GROUP_LOK= #{group_lok} 
	 AND LOK_KD= #{lok_kd}

