mapper.update:
UPDATE hak_biaya
DIVISI_KD = #{divisi_kd}, SUBDIV_KD = #{subdiv_kd}, KD_GROUP = #{kd_group}, KD_BIAYA = #{kd_biaya}, F_AKTIF = #{f_aktif}, DRTGL = #{drtgl}, SPTGL = #{sptgl}, SYS_USER_CREATE = #{user_create}, SYS_TGL_CREATE = #{tgl_create}, SYS_JAM_CREATE = #{jam_create}, SYS_USER_NONAKTIF = #{user_nonaktif}, SYS_TGL_NONAKTIF = #{tgl_nonaktif}, SYS_JAM_NONAKTIF = #{jam_nonaktif}
WHERE DIVISI_KD= #{divisi_kd} AND SUBDIV_KD= #{subdiv_kd} AND KD_GROUP= #{kd_group} AND KD_BIAYA= #{kd_biaya}

mapper.delete:
delete from hak_biaya
  
	DIVISI_KD= #{divisi_kd} 
	 AND SUBDIV_KD= #{subdiv_kd} 
	 AND KD_GROUP= #{kd_group} 
	 AND KD_BIAYA= #{kd_biaya}

