mapper.sql:
select * from (
	select  from lokasi C
)T

mapper.update:
UPDATE lokasi set TGL_TUTUP = #{tgl_tutup},
LOK_NM = #{lok_nm}, PROPINSI = #{propinsi}, KOTA = #{kota}, EMAIL = #{email}, F_TUTUP = #{f_tutup}, CTR_PPUC = #{ctr_ppuc}, MAX_CTR_PPUC = #{max_ctr_ppuc}, CTR_BATCH = #{ctr_batch}, MAX_CTR_BATCH = #{max_ctr_batch}, CTR_REALISASI = #{ctr_realisasi}, MAX_CTR_REALISASI = #{max_ctr_realisasi}, CURR_CTR_DATE = #{curr_ctr_date}, SYS_USER_UPDATE = #{user_update}, SYS_TGL_UPDATE = #{tgl_update}, SYS_JAM_UPDATE = #{jam_update}, SYS_USER_CREATE = #{user_create}, SYS_TGL_CREATE = #{tgl_create}
WHERE LOK_KD= #{lok_kd} AND DEPT_KD= #{dept_kd} AND SUBDIV_KD=#{subdiv_kd} AND DIVISI_KD = #{divisi_kd}

mapper.delete:
delete from lokasi 
  
	LOK_KD= #{lok_kd} 
	 AND DIVISI_KD= #{divisi_kd} 
	 AND SUBDIV_KD= #{subdiv_kd} 
	 AND DEPT_KD= #{dept_kd}

